// Extract form fields from multipart/form-data
// n8n webhook receives multipart data - form fields might be in different locations
const input = $input.item.json;

// Try multiple possible locations for form fields
let email = '';
let city = '';
let duration = '';
let startDate = '';

// Check various possible locations
if (input.body && typeof input.body === 'object') {
  email = input.body.email || email;
  city = input.body.city || city;
  duration = input.body.duration || duration;
  startDate = input.body.startDate || startDate;
}

// Also check direct properties
email = input.email || email;
city = input.city || city;
duration = input.duration || duration;
startDate = input.startDate || startDate;

// Check if body is a string (might need parsing)
if (typeof input.body === 'string') {
  try {
    const parsed = JSON.parse(input.body);
    email = parsed.email || email;
    city = parsed.city || city;
    duration = parsed.duration || duration;
    startDate = parsed.startDate || startDate;
  } catch (e) {
    // Not JSON, might be form-encoded
    console.log('Body is not JSON, trying form parsing');
  }
}

// Log for debugging
console.log('Extracted fields:', { email, city, duration, startDate });
console.log('Input structure:', JSON.stringify(Object.keys(input), null, 2));

// The PDF binary should be available as binary.itinerary.pdf
// Pass through both JSON fields and binary data
return {
  json: {
    email: email,
    city: city,
    duration: duration,
    startDate: startDate
  },
  binary: $input.item.binary || {}
};
