// Extract email, city from JSON and convert base64 PDF to binary
const webhookInput = $input.item.json;

// n8n webhook puts JSON payload in 'body' property
// Check both body and direct properties
const input = webhookInput.body || webhookInput;

console.log('=== DEBUG: Webhook Input Structure ===');
console.log('Webhook keys:', Object.keys(webhookInput));
console.log('Input keys:', Object.keys(input));
console.log('Has body?', !!webhookInput.body);
console.log('Has pdf?', !!(input.pdf && input.pdf.data));

// Extract form fields (now directly in JSON)
const email = input.email || '';
const city = input.city || 'Unknown City';
const duration = input.duration || '';
const startDate = input.startDate || '';

// Get base64 PDF data
let pdfBase64 = null;
if (input.pdf && input.pdf.data) {
  pdfBase64 = input.pdf.data; // Already base64 from backend
  console.log('PDF base64 data received, length:', pdfBase64.length);
} else {
  throw new Error('PDF data not found in input. Input keys: ' + Object.keys(input).join(', ') + '. Webhook keys: ' + Object.keys(webhookInput).join(', '));
}

// Validate email
if (!email) {
  throw new Error('Email not found in webhook input. Input structure: ' + JSON.stringify(Object.keys(input)));
}

// Convert base64 to Buffer for n8n binary format
const pdfBuffer = Buffer.from(pdfBase64, 'base64');
console.log('PDF buffer created, size:', pdfBuffer.length, 'bytes');

// Return JSON fields and binary PDF
// n8n Code node expects binary data as Buffer with data property
return {
  json: {
    email: email,
    city: city,
    duration: duration,
    startDate: startDate
  },
  binary: {
    'itinerary.pdf': {
      data: pdfBuffer, // Buffer object (n8n requires this)
      mimeType: 'application/pdf',
      fileName: 'itinerary.pdf'
    }
  }
};
